import React, { useState } from "react";
import { Button, Form, Input, Container } from "semantic-ui-react";

const validateRequired = (name, value) => {
  if (!value.toString().trim().length) {
    return {
      content: `Please enter a ${name}`,
    };
  }
};

// const email = (value) => {
//   if (!validator.isEmail(value)) {
//     return `${value} is not a valid email.`;
//   }
// };

const lt = (value, props) => {
  // get the maxLength from component's props
  if (!value.toString().trim().length > props.maxLength) {
    // Return jsx
    return (
      <span className="error">
        The value exceeded {props.maxLength} symbols.
      </span>
    );
  }
};

const ContactForm = () => {
  const [state, setState] = useState({});
  const handleChange = (e, { name, value }) => {
    var error;

    if (name === "email") {
      // error = validateEmail(value);
    }

    error = validateRequired(name, value);

    setState({ ...state, [name]: { value, error } });
  };
  // const [state, setState] = useState({});

  return (
    <Container>
      <Form>
        <Form.Field
          name="name"
          onChange={handleChange}
          id="form-input-control-error-name"
          label="Name"
          placeholder="Joe Schmoe"
          control={Input}
          error={state.name?.error}
        ></Form.Field>
        <Form.Field
          name="email"
          onChange={handleChange}
          id="form-input-control-error-email"
          control={Input}
          label="Email"
          placeholder="joe@schmoe.com"
          error={{
            content: "Please enter a valid email address",
            pointing: "below",
          }}
        ></Form.Field>
        <Form.Field
          name="message"
          onChange={handleChange}
          id="form-input-control-error-message"
          label="Message"
          control={Input}
          error={{
            content: "Please enter a message of at least 10 characters",
            pointing: "below",
          }}
        ></Form.Field>
        <Button type="submit">Submit</Button>
      </Form>
    </Container>
  );
};

export default ContactForm;
